    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try
        {
        String sql = "Insert into adddetails(productid,productname,quantity) VALUES (?,?,?)";
        con = DriverManager.getConnection("jdbc:mysql://localhost/mysql","bhargav","Gbhargav@944");
        pst = con.prepareStatement(sql);
        pst.setString(1,productid.getText());
        pst.setString(2,productname.getText());
        pst.setString(3,quantity.getText());
        pst.executeUpdate();
        JOptionPane.showMessageDialog(null,"Successfully Added");
        } catch (Exception ex) {
         JOptionPane.showMessageDialog(null,ex);   
        }
    }//GEN-LAST:event_jButton1ActionPerformed

