    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        try
        {
        String sql = "Insert into addfood(fooditem,category,foodname,amount,aproductname,aproductid,aquantity) VALUES (?,?,?,?,?,?,?)";
        con = DriverManager.getConnection("jdbc:mysql://localhost/mysql","bhargav","Gbhargav@944");
        pst = con.prepareStatement(sql);
        pst.setString(1,fooditem.getText());
        String value = category.getSelectedItem().toString();
        pst.setString(2,value);
        pst.setString(3,foodname.getText());
        pst.setString(4,amount.getText());
        String value1 = aproductname.getSelectedItem().toString();
        pst.setString(5,value1);
        pst.setString(6,aproductid.getText());
        pst.setString(7,aquantity.getText());
        pst.executeUpdate();
        JOptionPane.showMessageDialog(null,"Successfully Added");
        } catch (Exception ex) {
         JOptionPane.showMessageDialog(null,ex);   
        }
    }//GEN-LAST:event_jButton3ActionPerformed

